import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";

import { getPeos } from "../../actions/peos";
import { addPeoVision } from "../../actions/peo_visions";

export class MapForm extends Component {
  static propTypes = {
    peos: PropTypes.array.isRequired,
    getPeos: PropTypes.func.isRequired,
    addPeo_Vision: PropTypes.func.isRequired
  };

  state = {
    Peos: []
  };

  //component load then
  componentDidMount() {
    this.props.getPeos();
  }
  render() {
    let teamsFromApi = this.props.peo.map(peo => {
      return { value: peo, display: peo };
    });
    this.setState({
      Peos: [{ value: "", display: "(Select your favourite team)" }].concat(
        teamsFromApi
      )
    });

    return (
      <div>
        <select>
          {this.state.Peos.map(peo => (
            <option key={peo.value} value={peo.value}>
              {peo.display}
            </option>
          ))}
        </select>
      </div>
    );
  }
}

//state of redux
const mapStateToProps = state => ({
  peos: state.peos.peos,
  peo_visions: state.peo_visions.peo_visions
});
export default connect(mapStateToProps, {
  addPeoVision,
  getPeos
})(MapForm);
