import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addSubject } from "../../actions/subjects";
import { getPlos } from "../../actions/plos";

export class Form extends Component {
  //to part of component thats why we create state down there
  state = {
    code: "",
    subject_name: "",
    plos: null
  };
  static propTypes = {
    plos: PropTypes.func.isRequired,
    getPlos: PropTypes.func.isRequired,
    addSubject: PropTypes.func.isRequired
  };
  componentDidMount() {
    this.props.getPlos();
  }
  onChangeplos = selected => {
    console.log(selected);
    this.setState({
      plos: selected
    });
  };

  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { code, subject_name, plos } = this.state;
    const subject = { code, subject_name, plos };
    this.props.addSubject(subject);
  };
  render() {
    const { code, subject_name, plos } = this.state;
    let opt = [];
    console.log(this.props.plos.length);

    if (this.props.plos.length > 0) {
      for (let i in this.props.plos) {
        opt.push({
          value: this.props.plos[i].id,
          label: this.props.plos[i].code
        });
      }
    }
    return (
      <div>
        <h1>Add Subject</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>code</label>
            <input
              className="form-control"
              type="text"
              name="code"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <label>Subject Name</label>
            <input
              className="form-control"
              type="text"
              name="subject_name"
              onChange={this.onChange}
              value={subject_name}
            ></input>
            <label>SELECT PLOs</label>
            <Select
              value={plos}
              onChange={this.onChangeplos}
              options={opt}
              isMulti
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  plos: state.plos.plos
});

export default connect(mapStateToProps, { addSubject, getPlos })(Form);
