import { Form, Input, Icon, Button, Divider } from "antd";
import React from "react";
import QuestionForm from "./QuestionForm";
import { connect } from "react-redux";
import * as actions from "../../../actions/teacher_framework_actions/assignment";
class AssignmentCreate extends React.Component {
  state = {
    formCount: 1,
  };
  remove = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount - 1,
    });
  };

  add = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount + 1,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);
        const questions = [];
        for (let i = 0; i < values.questions.length; i += 1) {
          questions.push({
            title: values.question[i],
            choices: values.questions[i].choices.filter((el) => el !== null),
            answer: values.answers[i],
          });
        }
        const asnt = {
          teacher: this.props.username,
          title: values.titlte,
          questions,
        };
        console.log(asnt);
        //this.props.oncreateAssignment(asnt);
      }
    });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const questions = [];
    for (let i = 0; i < this.state.formCount; i += 1) {
      questions.push(
        <div key={i}>
          {questions.length > 0 ? (
            <Icon
              className="dynamic-delete-button"
              type="minus-circle-o"
              disabled={questions.length === 0}
              onClick={() => this.remove()}
            />
          ) : null}
          <QuestionForm id={i} {...this.props} />

          <Divider />
        </div>
      );
    }
    return (
      <Form onSubmit={this.handleSubmit}>
        <h>Assignment Create</h>
        <Form.Item label="Title:">
          {getFieldDecorator(`title`, {
            validateTrigger: ["onChange", "onBlur"],
            rules: [
              {
                required: true,
                message: "Please input title",
              },
            ],
          })(<Input placeholder="Title" />)}
        </Form.Item>
        {questions}
        <Form.Item>
          <Button type="secondary" onClick={this.add} style={{ width: "60%" }}>
            <Icon type="plus" /> Add More Questions
          </Button>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const WrappedAssignmentCreate = Form.create()(AssignmentCreate);

const mapStateToProps = (state) => {
  return {
    username: state.auth.user,
    loading: state.assignments.loading,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    oncreateAssignment: (id, asnt) =>
      dispatch(actions.createAssignment(id, asnt)),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(WrappedAssignmentCreate);
