import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addClos, getClos, deleteClos } from "../../actions/clos";
export class Clos extends Component {
  state = {
    code: "",
    description: ""
  };
  static propTypes = {
    clos: PropTypes.array.isRequired,
    getClos: PropTypes.func.isRequired,
    deleteClos: PropTypes.func.isRequired
  };
  //component load then
  componentDidMount() {
    this.props.getClos();
  }
  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { code, description } = this.state;
    const clo = { code, description };
    this.props.addClos(clo);
  };
  render() {
    const { code, description } = this.state;
    return (
      <div>
        <h1>Add CLO</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>SELECT CLO</label>
            <div className="drop-down">
              <input
                className="form-control"
                type="select"
                name="code"
                onChange={this.onChange}
                value={code}
              >
                {this.props.clos.map(clo => (
                  <option key={clo.id} value="clo.code">
                    {clo.code}
                  </option>
                ))}
              </input>
            </div>
          </div>
          <div className="form-group">
            <label>Description</label>
            <input
              className="form-control"
              type="text"
              name="description"
              onChange={this.onChange}
              value={description}
            ></input>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

//state of redux
const mapStateToProps = state => ({
  clos: state.clos.clos
});
export default connect(mapStateToProps, { addClos, getClos, deleteClos })(Clos);
