import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getClos, addClos } from "../../actions/clos";
import { getPeos, addPeo } from "../../actions/peos";
import { addPeoVision } from "../../actions/peo_visions";
import { Form } from "reactstrap";
import { Dropdown } from "semantic-ui-react";
export class MapForm extends Component {
  static propTypes = {
    clos: PropTypes.array.isRequired,
    getClos: PropTypes.func.isRequired,
    peos: PropTypes.array.isRequired,
    getPeos: PropTypes.func.isRequired,
    addPeo_Vision: PropTypes.func.isRequired
  };
  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { peo, vision } = this.state;
    const map = { peo, vision };
    this.props.addPeoVision(map);
  };
  constructor() {
    super();
    this.state = {
      // Clos: []
      Peos: []
    };
  }

  //component load then
  componentDidMount() {
    this.props.getPeos();
  }
  render() {
    const { code, peo, vision } = this.state;
    let optionItems = this.props.peos.map(peo => (
      <option key={peo.id}>{peo.code}</option>
    ));

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-4"></div>
          <div className="col-md-4">
            <Select
              name="code"
              value={code}
              onChange={this.onChange}
              options={optionItems}
            />
          </div>
          <div className="col-md-4"></div>
        </div>
      </div>
    );
  }
}

//state of redux
const mapStateToProps = state => ({
  clos: state.clos.clos,
  peos: state.peos.peos,
  peo_visions: state.peo_visions.peo_visions
});
export default connect(mapStateToProps, {
  getClos,
  addClos,
  addPeoVision,
  getPeos,
  addPeo
})(MapForm);
