import { Form, Input, Icon, Button } from "antd";
import React from "react";
import QuestionForm from "./QuestionForm";
class AssignmentCreate extends React.Component {
  state = {
    formCount: 1,
  };
  remove = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount - 1,
    });
  };

  add = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount + 1,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);
      }
    });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const questions = [];
    for (let i = 0; i < this.state.formCount; i += 1) {
      questions.push(<QuestionForm />);
    }
    return (
      <Form onSubmit={this.handleSubmit}>
        <h>Assignment Create</h>
        <Form.Item label="Title:">
          {getFieldDecorator(`title`, {
            validateTrigger: ["onChange", "onBlur"],
            rules: [
              {
                required: true,
                message: "Please input title",
              },
            ],
          })(<Input placeholder="Title" />)}
        </Form.Item>
        {questions}
        <Form.Item>
          <Button type="dashed" onClick={this.add} style={{ width: "60%" }}>
            <Icon type="plus" /> Add field
          </Button>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const WrappedAssignmentCreate = Form.create()(AssignmentCreate);
export default WrappedAssignmentCreate;
