import React from "react";
import { Form, Input, Icon, Button } from "antd";

const FormItem = Form.Item;

let id = 0;

class AssignmentCreate extends React.Component {
  remove = (k) => {
    const { form } = this.props;
    const keys = form.getFieldValue("keys");
    if (keys.length === 1) return;
    form.setFieldsValue({
      keys: keys.filter((key) => key !== k),
    });
  };

  add = () => {
    const { form } = this.props;
    const keys = form.getFieldValue("keys");
    const nextKeys = keys.concat(++id);
    form.setFieldsValue({
      keys: nextKeys,
    });
  };

  render() {
    const { getFieldDecorator, getFieldValue } = this.props.form;
    getFieldDecorator("keys", { initialValue: [] });
    const keys = getFieldValue("keys");
    const formItems = keys.map((k, index) => (
      <FormItem label={index === 0 ? "Question" : ""} key={k}>
        {getFieldDecorator(`names[${k}]`, {
          validateTrigger: ["onChange", "onBlur"],
          rules: [
            {
              required: true,
              whitespace: true,
              message: "Please input a question",
            },
          ],
        })(<Input placeholder="Question" />)}
        {keys.length > 1 ? (
          <Icon
            className="dynamic-delete-button"
            type="minus-circle-o"
            disabled={keys.length === 1}
            onClick={() => this.remove(k)}
          />
        ) : null}
      </FormItem>
    ));
    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <h1>Create an assignment</h1>
          <FormItem label={"Title:"}>
            {getFieldDecorator(`title`, {
              validateTrigger: ["onChange", "onBlur"],
              rules: [
                {
                  required: true,
                  message: "Please input a title ",
                },
              ],
            })(<Input placeholder="Add title" />)}
          </FormItem>
          {formItems}
          <FormItem>
            <Button type="secondary" onClick={this.add}>
              <Icon type="plus" /> Add question
            </Button>
          </FormItem>
          <FormItem>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </FormItem>
        </Form>
      </div>
    );
  }
}

const WrappedAssignmentCreate = Form.create()(AssignmentCreate);

export default WrappedAssignmentCreate;
