import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addPlo } from "../../actions/plos";
import { getPeos } from "../../actions/peos";

export class Form extends Component {
  state = {
    code: "",
    description: "",
    peos: null
  };
  static propTypes = {
    peos: PropTypes.func.isRequired,
    getPeos: PropTypes.func.isRequired,
    addPlo: PropTypes.func.isRequired
  };

  componentDidMount() {
    this.props.getPeos();
  }

  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { code, description } = this.state;
    const plo = { code, description };
    this.props.addPlo(plo);
  };
  render() {
    const { code, description } = this.state;
    return (
      <div>
        <h1>Add PLO</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>code</label>
            <input
              className="form-control"
              type="text"
              name="code"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <input
              className="form-control"
              type="text"
              name="description"
              onChange={this.onChange}
              value={description}
            ></input>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  peos: state.peos.peos
});

export default connect(mapStateToProps, { addPlo, getPeos })(Form);
