import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addVision } from "../../actions/visions";

export class Form extends Component {
  state = {
    misison: "",
    goal: "",
    outcome: ""
  };

  static propTypes = {
    addVision: PropTypes.func.isRequired
  };

  onChange = e =>
    this.setState({
      [e.target.mission]: e.target.value,
      [e.target.goal]: e.target.value,
      [e.target.outcome]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { mission, goal, outcome } = this.state;
    const vision = { goal, outcome, mission };
    this.props.addVision(vision);
  };
  render() {
    const { mission, goal, outcome } = this.state;
    return (
      <div>
        <h1>Add Vision</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>mission</label>
            <input
              className="form-control"
              type="text"
              name="mission"
              onChange={this.onChange}
              value={mission}
            ></input>
          </div>
          <div className="form-group">
            <label>goal</label>
            <input
              className="form-control"
              type="text"
              name="goal"
              onChange={this.onChange}
              value={goal}
            ></input>
          </div>
          <div className="form-group">
            <label>outcome</label>
            <input
              className="form-control"
              type="text"
              name="outcome"
              onChange={this.onChange}
              value={outcome}
            ></input>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default connect(null, { addVision })(Form);
