import React, { Component } from "react";
import { tokenConfig } from "../../actions/auth";
import axios from "axios";
import {
  Label,
  Input,
  Modal,
  ModalHeader,
  ModalFooter,
  ModalBody,
  Table,
  Button,
  FormGroup
} from "reactstrap";
class vision extends Component {
  state = {
    visions: [],
    newVisionData: {
      mission: "",
      goal: "",
      outcome: ""
    },
    editVisionData: {
      id: "",
      mission: "",
      goal: "",
      outcome: ""
    },

    newVisionModal: false,
    editVisionModal: false
  };
  componentWillMount() {
    this._refreshVisions();
  }
  //update state variable
  toggleNewVisionModal() {
    this.setState({
      newVisionModal: !this.state.newVisionModal
    });
    //this.state.newBookModal = true;
  }
  toggleEditVisionModal() {
    this.setState({
      editVisionModal: !this.state.editVisionModal
    });
    //this.state.newBookModal = true;
  }

  //add vision method
  addVision() {
    axios
      .post("http://localhost:8000/api/vision/", this.state.newVisionData)
      .then(response => {
        let { visions } = this.state;
        visions.push(response.data);
        this.setState({
          visions,
          newVisionModal: false,
          newVisionData: {
            mission: "",
            outcome: "",
            goal: ""
          }
        });
      });
  }
  updateVision() {
    let { outcome, mission, goal } = this.state.editVisionModal;
    axios
      .put("http://localhost:8000/api/vision/" + this.state.editVisionData.id, {
        outcome,
        mission,
        goal
      })
      .then(response => {
        this._refreshVisions();
        this.setState({
          editVisionModal: false,
          editVisionData: { id: "", outcome: "", mission: "", goal: "" }
        });
      });
  }

  editVision(id, mission, goal, outcome) {
    this.setState({
      editVisionData: { id, mission, goal, outcome },
      editVisionModal: !this.state.editVisionModal
    });
  }
  deleteVision(id) {
    axios.delete("http://localhost:8000/api/vision/" + id).then(response => {
      this._refreshVisions();
    });
  }

  _refreshVisions() {
    axios
      .get("http://localhost:8000/api/vision/", tokenConfig(state))
      .then(response => {
        this.setState({
          visions: response.data
        });
      });
  }

  render() {
    let visions = this.state.visions.map(vision => {
      return (
        <tr key={vision.id}>
          <td>{vision.id}</td>
          <td>{vision.mission}</td>
          <td>{vision.goal}</td>
          <td>{vision.outcome}</td>
          <td>
            <Button
              color="success"
              size="sm"
              className="mr-2"
              onClick={this.editVision.bind(
                this,
                vision.id,
                vision.mission,
                vision.goal,
                vision.outcome
              )}
            >
              Edit
            </Button>
            <Button
              color="danger"
              size="sm"
              onClick={this.deleteVision.bind(this, vision.id)}
            >
              Delete Vision
            </Button>
          </td>
        </tr>
      );
    });
    return (
      <div className="App container">
        <h1>Vision</h1>
        <Button
          className="my-3"
          color="danger"
          onClick={this.toggleNewVisionModal.bind(this)}
        >
          Add Vision
        </Button>
        <Modal
          isOpen={this.state.newVisionModal}
          toggle={this.toggleNewVisionModal.bind(this)}
        >
          {" "}
          <ModalHeader toggle={this.toggleNewVisionModal.bind(this)}>
            Add Vision
          </ModalHeader>
          <ModalBody>
            <FormGroup>
              <Label for="mission">Mission</Label>
              <Input
                id="mission"
                placeholder="Enter Mission"
                value={this.state.newVisionData.mission}
                onChange={e => {
                  let { newVisionData } = this.state;
                  newVisionData.mission = e.target.value;
                  this.setState({ newVisionData });
                }}
              />
            </FormGroup>
            <FormGroup>
              <Label for="goal">Goal</Label>
              <Input
                id="goal"
                placeholder="Enter Goal"
                value={this.state.newVisionData.goal}
                onChange={e => {
                  let { newVisionData } = this.state;
                  newVisionData.goal = e.target.value;
                  this.setState({ newVisionData });
                }}
              />
            </FormGroup>
            <FormGroup>
              <Label for="outcome">Outcome</Label>
              <Input
                id="outcome"
                placeholder="Enter Outcome"
                value={this.state.newVisionData.outcome}
                onChange={e => {
                  let { newVisionData } = this.state;
                  newVisionData.outcome = e.target.value;
                  this.setState({ newVisionData });
                }}
              />
            </FormGroup>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.addVision.bind(this)}>
              Add Vision
            </Button>{" "}
            <Button
              color="secondary"
              onClick={this.toggleNewVisionModal.bind(this)}
            >
              Cancel
            </Button>
          </ModalFooter>
        </Modal>

        <Modal
          isOpen={this.state.editVisionModal}
          toggle={this.toggleEditVisionModal.bind(this)}
        >
          {" "}
          <ModalHeader toggle={this.toggleEditVisionModal.bind(this)}>
            Add Vision
          </ModalHeader>
          <ModalBody>
            <FormGroup>
              <Label for="mission">Mission</Label>
              <Input
                id="mission"
                placeholder="Enter Mission"
                value={this.state.editVisionData.mission}
                onChange={e => {
                  let { editVisionData } = this.state;
                  editVisionData.mission = e.target.value;
                  this.setState({ editVisionData });
                }}
              />
            </FormGroup>
            <FormGroup>
              <Label for="goal">Goal</Label>
              <Input
                id="goal"
                placeholder="Enter Goal"
                value={this.state.editVisionData.goal}
                onChange={e => {
                  let { editVisionData } = this.state;
                  editVisionData.goal = e.target.value;
                  this.setState({ editVisionData });
                }}
              />
            </FormGroup>
            <FormGroup>
              <Label for="outcome">Outcome</Label>
              <Input
                id="outcome"
                placeholder="Enter Outcome"
                value={this.state.editVisionData.outcome}
                onChange={e => {
                  let { editVisionData } = this.state;
                  editVisionData.outcome = e.target.value;
                  this.setState({ editVisionData });
                }}
              />
            </FormGroup>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.updateVision.bind(this)}>
              Update Vision
            </Button>{" "}
            <Button
              color="secondary"
              onClick={this.toggleEditVisionModal.bind(this)}
            >
              Cancel
            </Button>
          </ModalFooter>
        </Modal>

        <Table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Mission</th>
              <th>Goal</th>
              <th>Outcome</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{visions}</tbody>
        </Table>
      </div>
    );
  }
}
export default vision;
