import React, { Component } from "react";
import { Form, Input, Button, Col, Row, Select } from "antd";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class Postquestions extends Component {
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  render() {
    const rules = [{ required: true }];

    const onFinish = (values) => {
      console.log("Received values of form:", values);
      this.props.questionSubmit(values);
    };
    const { Option } = Select;

    function handleChange(value) {
      console.log(`selected ${value}`);
    }
    return (
      <Form onFinish={onFinish} className="my-form">
        <Form.Item
          name={["subject_name"]}
          fieldKey={["subject_name"]}
          rules={rules}
        >
          <Select
            defaultValue="lucy"
            style={{ width: 120 }}
            onChange={handleChange}
          >
            <Option value="computer">Jack</Option>
            <Option value="physics">Lucy</Option>
          </Select>
        </Form.Item>
        <Form.List name="question_set">
          {(fields, { add, remove }) => {
            return (
              <div>
                {fields.map((field, index) => (
                  <Row key={field.key}>
                    <Col>
                      <Form.Item
                        name={[field.name, "label"]}
                        fieldKey={[field.fieldKey, "label"]}
                        rules={rules}
                      >
                        <Input placeholder="Enter Question" />
                      </Form.Item>
                    </Col>
                    <Col></Col>
                    <Col flex="none">
                      <MinusCircleOutlined
                        className="dynamic-delete-button"
                        onClick={() => {
                          remove(field.name);
                        }}
                      />
                    </Col>
                  </Row>
                ))}
                <Form.Item>
                  <Button
                    type="dashed"
                    onClick={() => {
                      add();
                    }}
                    style={{ width: "100%" }}
                  >
                    <PlusOutlined /> Add Questions
                  </Button>
                </Form.Item>
              </div>
            );
          }}
        </Form.List>

        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

export default Postquestions;
