import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getPeos, addPeo } from "../../actions/peos";
import { addPeoVision } from "../../actions/peo_visions";

export class MapForm extends Component {
  static propTypes = {
    peos: PropTypes.array.isRequired,
    getPeos: PropTypes.func.isRequired,
    addPeo_Vision: PropTypes.func.isRequired
  };
  constructor() {
    super();
    this.state = {
      // Clos: []
      Peos: []
    };
  }

  //component load then
  componentDidMount() {
    fetch("http://localhost:8000/api/peo")
      .then(response => {
        return response.json();
      })
      .then(data => {
        let teamsFromApi = data.map(peo => {
          return { value: peo, display: peo };
        });
        this.setState({
          Peos: [{ value: "", display: "(Select your favourite peo)" }].concat(
            teamsFromApi
          )
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <select>
          {this.state.peos.map(peo => (
            <option key={peo.value} value={peo.value}>
              {peo.display}
            </option>
          ))}
        </select>
      </div>
    );
  }
}

//state of redux
const mapStateToProps = state => ({
  peos: state.peos.peos,
  peo_visions: state.peo_visions.peo_visions
});
export default connect(mapStateToProps, {
  addPeoVision,
  getPeos,
  addPeo
})(MapForm);
