import axios from "axios";

import { GET_PROJECTS, DELETE_PROJECTS, ADD_PROJECTS } from "./types";

//action method for get projects

export const getProjects = () => (dispatch) => {
  axios
    .get("/record")
    .then((res) => {
      dispatch({
        type: GET_PROJECTS,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
//delete Projects

export const deleteProjects = (id) => (dispatch) => {
  axios
    .delete(`/record/${id}/`)
    .then((res) => {
      dispatch({
        type: DELETE_PROJECTS,
        payload: id,
      });
    })
    .catch((err) => console.log(err));
};

export const addProjects = (project) => (dispatch) => {
  axios
    .get("/record")
    .then((res) => {
      dispatch({
        type: ADD_PROJECTS,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
