import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";

import { getPeos } from "../../actions/peos";
import { addPeoVision } from "../../actions/peo_visions";

export class MapForm extends Component {
  static propTypes = {
    peos: PropTypes.array.isRequired,
    getPeos: PropTypes.func.isRequired,
    addPeo_Vision: PropTypes.func.isRequired
  };

  state = {
    Peos: [],
    selectedPeo: "",
    validationError: ""
  };

  //component load then
  componentDidMount() {
    this.props.getPeos();
  }
  render() {
    const { code, peo, vision } = this.state;
    let optionItems = this.props.peos.map(peo => (
      <option key={peo.id}>{peo.code}</option>
    ));

    return (
      <div>
        <select
          value={this.state.selectedTeam}
          onChange={e =>
            this.setState({
              selectedTeam: e.target.value,
              validationError:
                e.target.value === ""
                  ? "You must select your favourite team"
                  : ""
            })
          }
        >
          {optionItems}
        </select>
      </div>
    );
  }
}

//state of redux
const mapStateToProps = state => ({
  peos: state.peos.peos,
  peo_visions: state.peo_visions.peo_visions
});
export default connect(mapStateToProps, {
  addPeoVision,
  getPeos
})(MapForm);
