import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addSubject } from "../../actions/subjects";
import { getPlos } from "../../actions/plos";

export class Form extends Component {
  //to part of component thats why we create state down there
  state = {
    year: "",
    calendar_year: "",
    code: "",
    tittle: "",
    plos: null
  };
  static propTypes = {
    plos: PropTypes.func.isRequired,
    getPlos: PropTypes.func.isRequired,
    addSubject: PropTypes.func.isRequired
  };
  componentDidMount() {
    this.props.getPlos();
  }
  onChangeplos = selected => {
    // console.log(selected);
    this.setState({
      plos: selected
    });
  };

  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { year, calendar_year, code, tittle, plos } = this.state;
    const subject = { year, calendar_year, code, tittle, plos };
    subject.plos = [];
    for (let i in plos) {
      subject.plos.push(plos[i].value);
    }
    console.log(subject);
    this.props.addSubject(subject);
  };
  render() {
    const { year, calendar_year, code, tittle, plos } = this.state;
    let opt = [];
    console.log(this.props.plos.length);

    if (this.props.plos.length > 0) {
      for (let i in this.props.plos) {
        opt.push({
          value: this.props.plos[i].id,
          label: this.props.plos[i].code
        });
      }
    }
    return (
      <div>
        <h1>Add Subject</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Year</label>
            <input
              className="form-control"
              type="text"
              name="year"
              onChange={this.onChange}
              value={year}
            ></input>
          </div>
          <div className="form-group">
            <label>Calendar Year</label>
            <input
              className="form-control"
              type="text"
              name="calendar_year"
              onChange={this.onChange}
              value={calendar_year}
            ></input>
          </div>
          <div className="form-group">
            <label>code</label>
            <input
              className="form-control"
              type="text"
              name="code"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <label>Subject Name</label>
            <input
              className="form-control"
              type="text"
              name="tittle"
              onChange={this.onChange}
              value={tittle}
            ></input>
            <label>SELECT PLOs</label>
            <Select
              value={plos}
              onChange={this.onChangeplos}
              options={opt}
              isMulti
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  plos: state.plos.plos
});

export default connect(mapStateToProps, { addSubject, getPlos })(Form);
