import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import TagsInput from "react-tagsinput";
import { addCongitive } from "../../../actions/chairman_framework_actions/cognitive";
export class POST extends Component {
  state = {
    code: "",
    definition: "",
    tags: null
  };
  static propTypes = {
    addCongitive: PropTypes.func.isRequired
  };

  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { code, definition, tags } = this.state;
    const cognitive = { code, definition, tags };
    this.props.addCongitive(cognitive);
  };
  handleChange(tags) {
    this.setState({ tags });
  }

  render() {
    const { code, definition, tags } = this.state;
    return (
      <div>
        <h1>Add Cognitive Domain</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Code</label>
            <input
              className="form-control"
              type="text"
              name="code"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <label>Definition</label>
            <input
              className="form-control"
              type="text"
              name="definition"
              onChange={this.onChange}
              value={definition}
            ></input>
          </div>
          <div>
            <label>Enter Verbs</label>
            <TagsInput
              name="tags"
              value={this.state.tags}
              onChange={this.handleChange}
            />
          </div>

          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default connect(null, { addCongitive })(POST);
