import React, { Component } from "react";
import axios from "axios";
import ModalHello from "./Modal";
import {
  Modal,
  ModalHeader,
  ModalFooter,
  ModalBody,
  Table,
  Button
} from "reactstrap";
class App extends Component {
  state = {
    visions: [],
    newVisionModal: false
  };
  componentWillMount() {
    axios.get("http://localhost:8000/api/vision/").then(response => {
      this.setState({
        visions: response.data //response by server
      });
    });
  }
  //update state variable
  toggleNewVisionModal() {
    this.setState({
      newVisionModal: true
    });
    //this.state.newBookModal = true;
  }
  render() {
    let visions = this.state.visions.map(vision => {
      return (
        <tr key={vision.id}>
          <td>{vision.id}</td>
          <td>{vision.mission}</td>
          <td>{vision.goal}</td>
          <td>{vision.outcome}</td>
          <td>
            <Button color="success" size="sm" className="mr-2">
              Edit
            </Button>
            <Button color="danger" size="sm">
              Delete
            </Button>
          </td>
        </tr>
      );
    });
    return (
      <div className="App container">
        <ModalHello />
        <Button color="danger" onClick={this.toggleNewVisionModal.bind(this)}>
          Add Vision
        </Button>
        <Modal
          isOpen={this.state.newVisionModal}
          toggle={this.toggleNewVisionModal.bind(this)}
        >
          {" "}
          >
          <ModalHeader toggle={this.toggleNewVisionModal.bind(this)}>
            Add Vision
          </ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat. Duis aute irure dolor in
            reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
            pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
            culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button
              color="primary"
              onClick={this.toggleNewVisionModal.bind(this)}
            >
              Do Something
            </Button>{" "}
            <Button
              color="secondary"
              onClick={this.toggleNewVisionModal.bind(this)}
            >
              Cancel
            </Button>
          </ModalFooter>
        </Modal>

        <Table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Mission</th>
              <th>Goal</th>
              <th>Outcome</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{visions}</tbody>
        </Table>
      </div>
    );
  }
}
export default App;
