import axios from "axios";
import {
  GET_Assignment,
  DELETE_Assignment,
  ADD_Assignment,
  GET_ASSIGNMENT_DETAIL_FAIL,
  GET_ASSIGNMENT_DETAIL_SUCCESS,
  GET_ASSIGNMENT_DETAIL_START,
} from "../types";
import { tokenConfig } from "../auth";
export const getAssignment = () => (dispatch, getState) => {
  //asy request with axios we are making here
  console.log(getState);

  axios
    .get("http://127.0.0.1:8000/api/assignment/", tokenConfig(getState))

    .then((res) => {
      dispatch({
        type: GET_Assignment, //in action we are passing type action.type
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
//delete plo
export const deleteAssignment = (id) => (dispatch, getState) => {
  axios
    .delete("http://localhost:8000/api/assignment/" + id, tokenConfig(getState))
    .then((res) => {
      dispatch({
        type: DELETE_Assignment,
        payload: id,
      });
    })
    .catch((err) => console.log(err));
};
//ADD plo
export const addAssignment = (assignment) => (dispatch, getState) => {
  axios
    .post(
      "http://127.0.0.1:8000/api/assignment/",
      assignment,
      tokenConfig(getState)
    )
    .then((res) => {
      dispatch({
        type: ADD_Assignment,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};

////// ASSIGNMENT DEATIL
const getASNTDetailStart = () => {
  return {
    type: GET_ASSIGNMENT_DETAIL_START,
  };
};

const getASNTDetailSuccess = (assignment) => {
  return {
    type: GET_ASSIGNMENT_DETAIL_SUCCESS,
    assignment,
  };
};

const getASNTDetailFail = (error) => {
  return {
    type: GET_ASSIGNMENT_DETAIL_FAIL,
    error: error,
  };
};

export const getASNTSDetail = (token, id) => {
  return (dispatch) => {
    dispatch(getASNTDetailStart());
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${token}`,
    };
    axios
      .get("http://127.0.0.1:8000/assignment/" + id)
      .then((res) => {
        const assignment = res.data;
        dispatch(getASNTDetailSuccess(assignment));
      })
      .catch((err) => {
        dispatch(getASNTDetailFail());
      });
  };
};
