import { Card, Skeleton } from "antd";
import React from "react";
import { connect } from "react-redux";
import * as actions from "../../../actions/teacher_framework_actions/assignment";
import Questions from "./Questions";
class AssignmentDetail extends React.PureComponent {
  componentDidMount() {
    this.props.ongetAssignementDetail(this.props.match.params.id);
    console.log(this.props);
  }
  render() {
    const { currentAssignment } = this.props;
    const { title } = currentAssignment;
    return (
      <div>
        {this.props.loading ? (
          <Skeleton active />
        ) : (
          <Card title={title}>
            <Questions
              questions={currentAssignment.questions.map((q) => {
                return (
                  <Card
                    type="inner"
                    key={q.id}
                    title={`${q.order}.${q.question}`}
                  >
                    {" "}
                  </Card>
                );
              })}
            />
          </Card>
        )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    currentAssignment: state.assignments.currentAssignment,
    loading: state.assignments.loading,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    ongetAssignementDetail: (id) => dispatch(actions.getAssignementDetail(id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AssignmentDetail);
