import axios from "axios";

import { GET_PROJECTS } from "./types";

//action method for get projects

export const getProjects = () => (dispatch) => {
  axios
    .get("/api/record/")
    .then((res) => {
      dispatch({
        type: GET_PROJECTS,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
