import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addVision } from "../../actions/visions";
import { getPeos } from "../../actions/peos";

export class Form extends Component {
  state = {
    vision: "",
    peos: "",
    selectedOption: null
  };

  static propTypes = {
    peos: PropTypes.func.isRequired,
    addVision: PropTypes.func.isRequired,
    getPeos: PropTypes.func.isRequired
  };

  componentDidMount() {
    this.props.getPeos();
  }

  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { vision, peos } = this.state;
    const visions = { vision, peos };
    visions.peos = [peos];
    console.log(visions);
    this.props.addVision(visions);
  };
  render() {
    console.log(this.props.peos);
    const { vision, peos, selectedOption } = this.state;
    let optionItems = this.props.peos.map(peo => (
      <option value={peo.id} key={peo.id}>
        {peo.code}
      </option>
    ));
    return (
      <div>
        <h1>Add vision</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>vision</label>
            <input
              className="form-control"
              type="text"
              name="vision"
              onChange={this.onChange}
              value={vision}
            ></input>
            <Select
              value={selectedOption}
              onChange={this.onChange}
              options={optionItems}
            />

            <select name="peos" onChange={this.onChange}>
              {optionItems}
            </select>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  peos: state.peos.peos
});

export default connect(mapStateToProps, { addVision, getPeos })(Form);
