import React from "react";
import { Form, Input, Button } from "antd";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";
const FormItem = Form.Item;
import QuestionForm from "./QuestionForm";
class AssignmentCreate extends React.Component {
  state = {
    formCount: 1,
  };
  remove = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount - 1,
    });
  };

  add = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount + 1,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const questions = [];
    for (let i = 0; i < this.state.formCount; i += 1) {
      questions.push(<QuestionForm key={i} {...this.props} />);
    }

    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <h1>Create an assignment</h1>
          <FormItem label={"Title:"}>
            {getFieldDecorator(`title`, {
              validateTrigger: ["onChange", "onBlur"],
              rules: [
                {
                  required: true,
                  message: "Please input a title ",
                },
              ],
            })(<Input placeholder="Add title" />)}
          </FormItem>
          {questions}
          <FormItem>
            <Button type="secondary" onClick={this.add}>
              <PlusOutlined /> Add Question
            </Button>
          </FormItem>
          <FormItem>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </FormItem>
        </Form>
      </div>
    );
  }
}

const WrappedAssignmentCreate = Form.create()(AssignmentCreate);

export default WrappedAssignmentCreate;
