import React, { Component } from "react";
import axios from "axios";
import "./FullPost.css";

class FullPost extends Component {
  state = {
    loadedPosts: null,
  };
  componentDidUpdate() {
    if (this.props.id) {
      if (
        !this.state.loadedPosts ||
        (this.state.loadedPosts && this.state.loadedPosts.id !== this.props.id)
      ) {
        axios.get("record/" + this.props.id).then((response) => {
          console.log(response);
          this.setState({ loadedPosts: response.data });
        });
      }
    }
  }

  deletePostHandler = () => {
    axios.delete("record/" + this.props.id).then((response) => {
      console.log(response);
    });
  };
  render() {
    let post = <p>Please select a project to see its discription!</p>;
    if (this.props.id) {
      post = <p>Loading !!!</p>;
    }
    if (this.state.loadedPosts) {
      post = (
        <div className="FullPost">
          <h1>{this.state.loadedPosts.Project_Name}</h1>
          <p>{this.state.loadedPosts.Project_Supervisor}</p>
          <div className="Edit">
            <button className="Delete" onClick={this.deletePostHandler}>
              Delete
            </button>
          </div>
        </div>
      );
    }

    return post;
  }
}

export default FullPost;
