import { Form, Input, Icon, Button, Divider } from "antd";
import React from "react";
import Select from "react-select";
import QuestionForm from "./QuestionForm";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";
import * as actions from "../../../actions/teacher_framework_actions/assignment";
class AssignmentCreate extends React.Component {
  state = {
    formCount: 1,
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
  };

  componentDidMount() {
    console.log(this.props.getAssigned());
  }
  onChangesubjects = (selected) => {
    console.log(selected);
    this.setState({
      subject_name: selected,
    });
  };
  remove = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount - 1,
    });
  };

  add = () => {
    const { formCount } = this.state;
    this.setState({
      formCount: formCount + 1,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);

        const question_set = [];

        for (let i in values.question) {
          const choices = [];
          for (let j of values.questions[i].choices) {
            if (j) {
              choices.push({
                choice_text: j,
              });
            }
          }
          question_set.push({
            question_text: values.question[i],
            answer: values.answers[i],
            choice_set: choices,
          });
        }
        const asnt = {
          title: values.title,
          question_set,
        };
        console.log(asnt);
        //this.props.oncreateAssignment(asnt);
      }
    });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const questions = [];
    for (let i = 0; i < this.state.formCount; i += 1) {
      questions.push(
        <div key={i}>
          {questions.length > 0 ? (
            <Icon
              className="dynamic-delete-button"
              type="minus-circle-o"
              disabled={questions.length === 0}
              onClick={() => this.remove()}
            />
          ) : null}
          <QuestionForm id={i} {...this.props} />

          <Divider />
        </div>
      );
    }
    let optionsubjects = [];
    if (
      this.props.assignedsubjects.length > 0 &&
      this.props.assignedsubjects[0].subjects.length > 0
    ) {
      // console.log(true, this.props.assignedsubjects[0].subjects);
      for (let i in this.props.assignedsubjects[0].subjects) {
        optionsubjects.push({
          value: this.props.assignedsubjects[0].subjects[i].id,
          label: this.props.assignedsubjects[0].subjects[i].tittle,
        });
      }
    }
    console.log(optionsubjects);
    return (
      <Form onSubmit={this.handleSubmit}>
        <h1>Assignment Create</h1>
        <Form.Item label="Title:">
          {getFieldDecorator(`title`, {
            validateTrigger: ["onChange", "onBlur"],
            rules: [
              {
                required: true,
                message: "Please input title",
              },
            ],
          })(
            <Select onChange={this.onChangesubjects} options={optionsubjects} />
          )}
        </Form.Item>
        {questions}
        <Form.Item>
          <Button type="secondary" onClick={this.add} style={{ width: "60%" }}>
            <Icon type="plus" /> Add More Questions
          </Button>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const WrappedAssignmentCreate = Form.create()(AssignmentCreate);

const mapStateToProps = (state) => {
  return {
    username: state.auth.user,
    loading: state.assignments.loading,
    assignedsubjects: state.assignedsubjects.assignedsubjects,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    oncreateAssignment: (asnt) => dispatch(actions.createAssignment(asnt)),
    getAssigned,
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(WrappedAssignmentCreate);
