import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class POST extends Component {
  state = {
    subject_name: null,
    question_set: "",
    selectedsubject: null,
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }

  onChangeverbs = (selected) => {
    //console.log(selected);
    this.setState({
      verbs: selected,
    });
  };

  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name, question_set } = this.state;
    const assignment = {
      subject_name,

      question_set,
    };
    assignment.question_set = [];
    for (let i in question_set) {
      assignment.question_set.push(question_set[i].value);
    }

    console.log(assignment);
    //this.props.addClos(clo);
  };

  subjectoptions = () => {
    /*
    if (this.state.selectedid === null) {
      return null;
    }
    */

    if (this.state.assignedsubjects === null) {
      return null;
    }
    let subjectoptions = [];
    if (this.state.assignedsubjects !== null) {
      for (let i in this.props.assignedsubjects) {
        if (
          this.state.assignedsubjects.tittle ===
          this.props.assignedsubjects[i].id
        ) {
          for (let j in this.props.assignedsubjects[i].tittle) {
            subjectoptions.push({
              value: this.props.assignedsubjects[i].tittle[j],
              label: this.props.assignedsubjects[i].tittle[j],
            });
          }
        }
      }
    }

    console.log(subjectoptions);
    //return verbsoptions;
  };

  render() {
    //console.log(this.verbsoptions());
    // console.log(this.state.cognitives);
    // console.log(this.props.cognitives);
    const { subject_name, question_set, label } = this.state;

    //console.log(this.props.plos.length);
    return (
      <div>
        <h1>Add Assignemnt</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Subject Name</label>
            <Select
              value={this.state.subject_name}
              onChange={this.onChangeverbs}
              options={this.subjectoptions()}
            />
          </div>
          <div className="form-group">
            <label>Questions</label>
            <input
              className="form-control"
              type="text"
              name="label"
              onChange={this.onChange}
              value={label}
            ></input>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  assignedsubjects: state.assignedsubjects.assignedsubjects,
});

export default connect(mapStateToProps, {
  addAssignment,
  getAssigned,
})(POST);
