import { GET_Quizz, ADD_Quizz, DELETE_Quizz } from "../../actions/types";

const intialState = {
  quizzes: [],
};

export default function (state = intialState, action) {
  switch (action.type) {
    case GET_Quizz:
      return {
        ...state,
        quizzes: action.payload,
      };
      console.log(action.payload);

    case DELETE_Quizz:
      return {
        ...state,
        quizzes: state.quizzes.filter((quizz) => quizz.id !== action.payload),
      };
    case ADD_Quizz:
      return {
        ...state,
        quizzes: [...state.quizzes, action.payload],
      };

    default:
      return state;
  }
}
