import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAffective } from "../../../actions/chairman_framework_actions/affectives";
export class POST extends Component {
  state = {
    code: "",
    definition: ""
  };
  static propTypes = {
    addAffective: PropTypes.func.isRequired
  };
  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { code, definition } = this.state;
    const affectives = { code, definition };
    this.props.addAffective(affectives);
  };
  render() {
    const { code, definition } = this.state;
    return (
      <div>
        <h1>Add Affective Domain</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Code</label>
            <input
              className="form-control"
              type="text"
              name="code"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <label>Definition</label>
            <input
              className="form-control"
              type="text"
              name="definition"
              onChange={this.onChange}
              value={definition}
            ></input>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default connect(null, { addAffective })(POST);
