import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addVision } from "../../actions/visions";
import { getPeos } from "../../actions/peos";

export class Form extends Component {
  state = {
    vision: ""
  };

  static propTypes = {
    addVision: PropTypes.func.isRequired
  };

  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { vision } = this.state;
    const visions = { vision };
    this.props.addVision(visions);
  };
  render() {
    const { vision } = this.state;
    return (
      <div>
        <h1>Add Vision</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Vision</label>
            <input
              className="form-control"
              type="text"
              name="vision"
              onChange={this.onChange}
              value={vision}
            ></input>
          </div>

          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  peos: state.peos.peos,
  visions: state.visions.visions
});
export default connect(mapStateToProps, {
  getPeos,
  addVision
})(Form);
