import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class POST extends Component {
  state = {
    subject_name: null,
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }
  onChangesubjects = (selected) => {
    console.log(selected);
    this.setState({
      assignedsubjects: selected,
    });
  };

  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name } = this.state;
    const quizz = {
      subject_name,
    };
    quizz.plos = [];
    for (let i in plos) {
      quizz.plos.push(plos[i].value);
    }

    console.log(quizz);
    //this.props.addClos(clo);
  };

  render() {
    //console.log(this.verbsoptions());
    // console.log(this.state.cognitives);
    // console.log(this.props.cognitives);
    const { subject_name } = this.state;
    let optionsubjects = [];
    if (this.props.plos.length > 0) {
      for (let i in this.props.plos) {
        optionsubjects.push({
          value: this.props.plos[i].id,
          label: this.props.plos[i].tittle,
        });
      }
    }

    return (
      <div>
        <h1>CREATE QUIZZ</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Select SUBJECT</label>
            <Select
              value={subject_name}
              onChange={this.onChangesubjects}
              options={optionsubjects}
              isMulti
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  assignedsubjects: state.assignedsubjects.assignedsubjects,
});

export default connect(mapStateToProps, {
  addAssignment,
  getAssigned,
})(POST);
