import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addVision } from "../../actions/visions";
import { getPeos } from "../../actions/peos";

export class Form extends Component {
  state = {
    vision: "",
    peos: ""
  };

  static propTypes = {
    addVision: PropTypes.func.isRequired,
    peos: PropTypes.array.isRequired,
    getPeos: PropTypes.func.isRequired
  };

  onChange = e => {
    // console.log(e.target.id, e.target.name, e.target.value);
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  onSubmit = e => {
    e.preventDefault();
    const { vision, peos } = this.state;
    const map = { vision, peos };
    console.log(map);
    this.props.addVision(map);
  };

  //component to get peos
  componentDidMount() {
    this.props.getPeos();
  }

  render() {
    // console.log(this.props.visions);
    // console.log(this.props.peos);
    const { vision, peos } = this.state;
    let optionItems = this.props.peos.map(peo => (
      <option key={peo.id}>{peo.code}</option>
    ));
    return (
      <div>
        <h1>Add Vision</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Vision</label>
            <input
              className="form-control"
              type="text"
              name="vision"
              onChange={this.onChange}
              value={vision}
            ></input>
            <select
              name="peos"
              onChange={this.onChange}
              value={peos}
              option={optionItems}
            >
              {optionItems}
            </select>
          </div>

          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  peos: state.peos.peos,
  visions: state.visions.visions
});
export default connect(mapStateToProps, {
  getPeos,
  addVision
})(Form);
