import React, { Component } from "react";
import { Form, Input, Button } from "antd";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class Postquestions extends Component {
  state = {
    subject_name: null,
    question_set: [],
    label: "",
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }
  onChangesubjects = (selected) => {
    console.log(selected);
    this.setState({
      subject_name: selected,
    });
  };

  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name, question_set } = this.state;
    const quizz = {
      subject_name,
      question_set,
    };
    quizz.question_set = [];
    for (let i in question_set) {
      quizz.question_set.push(question_set[i].value);
    }
    quizz.subject_name = [];
    for (let i in subject_name) {
      quizz.subject_name.push(subject_name[i].value);
    }

    console.log(quizz);
    //this.props.addClos(clo);
  };

  render() {
    //console.log(this.verbsoptions());
    //console.log(this.state.subject_name);
    console.log(this.props.assignedsubjects);
    const { subject_name, label } = this.state;

    let optionquestions = [];

    let optionsubjects = [];
    if (
      this.props.assignedsubjects.length > 0 &&
      this.props.assignedsubjects[0].subjects.length > 0
    ) {
      // console.log(true, this.props.assignedsubjects[0].subjects);
      for (let i in this.props.assignedsubjects[0].subjects) {
        optionsubjects.push({
          value: this.props.assignedsubjects[0].subjects[i].id,
          label: this.props.assignedsubjects[0].subjects[i].tittle,
        });
      }
    }
    console.log(optionsubjects);
    return (
      <Form name="dynamic_form_item">
        <FormItem label={"Title: "}>
          {getFieldDecorator(`title`, {
            validateTrigger: ["onChange", "onBlur"],
            rules: [
              {
                required: true,
                message: "Please input a title",
              },
            ],
          })(<Input placeholder="Add a title" />)}
        </FormItem>
        <Form.List name="names">
          {(fields, { add, remove }) => {
            return (
              <div>
                {fields.map((field, index) => (
                  <Form.Item
                    label={index === 0 ? "Passengers" : ""}
                    required={false}
                    key={field.key}
                  >
                    <Form.Item
                      {...field}
                      validateTrigger={["onChange", "onBlur"]}
                      rules={[
                        {
                          required: true,
                          whitespace: true,
                          message:
                            "Please input question's name or delete this field.",
                        },
                      ]}
                      noStyle
                    >
                      <Input
                        placeholder="passenger name"
                        style={{ width: "60%" }}
                      />
                    </Form.Item>
                    {fields.length > 1 ? (
                      <MinusCircleOutlined
                        className="dynamic-delete-button"
                        style={{ margin: "0 8px" }}
                        onClick={() => {
                          remove(field.name);
                        }}
                      />
                    ) : null}
                  </Form.Item>
                ))}
                <Form.Item>
                  <Button
                    type="dashed"
                    onClick={() => {
                      add();
                    }}
                    style={{ width: "60%" }}
                  >
                    <PlusOutlined /> Add field
                  </Button>
                </Form.Item>
              </div>
            );
          }}
        </Form.List>

        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const mapStateToProps = (state) => ({
  assignedsubjects: state.assignedsubjects.assignedsubjects,
});

export default connect(mapStateToProps, {
  addAssignment,
  getAssigned,
})(Postquestions);
