import React, { Component } from "react";
import { Form, Input, Button, Col, Row } from "antd";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

export class Postquestions extends Component {
  state = {
    subject_name: null,
    question_set: [],
    label: "",
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }
  onChangesubjects = (selected) => {
    console.log(selected);
    this.setState({
      subject_name: selected,
    });
  };

  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name } = this.state;
    const quizz = {
      subject_name,
    };

    console.log(quizz);
    //this.props.addClos(clo);
  };

  render() {
    const rules = [{ required: true }];

    const onFinish = (values) => {
      console.log("Received values of form:", values);
    };

    return (
      <Form onFinish={onFinish} className="my-form">
        <Select
          value={subject_name}
          onChange={this.onChangesubjects}
          options={optionsubjects}
        />
        <Form.List name="question_set">
          {(fields, { add, remove }) => {
            return (
              <div>
                {fields.map((field, index) => (
                  <Row key={field.key}>
                    <Col>
                      <Form.Item
                        name={[field.name, "label"]}
                        fieldKey={[field.fieldKey, "label"]}
                        rules={rules}
                      >
                        <Input placeholder="Enter Question" />
                      </Form.Item>
                    </Col>
                    <Col flex="none">
                      <MinusCircleOutlined
                        className="dynamic-delete-button"
                        onClick={() => {
                          remove(field.name);
                        }}
                      />
                    </Col>
                  </Row>
                ))}
                <Form.Item>
                  <Button
                    type="dashed"
                    onClick={() => {
                      add();
                    }}
                    style={{ width: "100%" }}
                  >
                    <PlusOutlined /> Add Questions
                  </Button>
                </Form.Item>
              </div>
            );
          }}
        </Form.List>

        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const mapStateToProps = (state) => ({
  assignedsubjects: state.assignedsubjects.assignedsubjects,
});

export default connect(mapStateToProps, {
  addAssignment,
  getAssigned,
})(Postquestions);
