import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class POST extends Component {
  state = {
    subject_name: null,
  };
  static propTypes = {
    subject_name: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }
  onChangeSubject = (selected) => {
    console.log(selected);
    this.setState({
      subject_name: selected,
    });
  };
  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name } = this.state;
    const assignment = { subject_name };
    assignment.subject_name = [];
    for (let i in subject_name) {
      assignment.subject_name.push(subject_name[i].value);
    }
    console.log(assignment);
    //this.props.addPlo(plo);
  };
  render() {
    const { code, description, peos } = this.state;
    let opt = [];
    console.log(this.props.peos.length);

    if (this.props.peos.length > 0) {
      for (let i in this.props.peos) {
        opt.push({
          value: this.props.peos[i].id,
          label: this.props.peos[i].code,
        });
      }
    }
    return (
      <div>
        <h1>Add PLO</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>code</label>
            <input
              className="form-control"
              type="text"
              name="code"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <input
              className="form-control"
              type="text"
              name="description"
              onChange={this.onChange}
              value={description}
            ></input>
            <label>Select PEO</label>
            <Select
              value={peos}
              onChange={this.onChangepeos}
              options={opt}
              isMulti
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  peos: state.peos.peos,
});

export default connect(mapStateToProps, { addPlo, getPeos })(POST);
