import { GET_MISSIONS, DELETE_MISSIONS, ADD_MISSIONS } from "../actions/types";

const intialState = {
  visions: []
};

export default function(state = intialState, action) {
  switch (action.type) {
    case GET_MISSIONS:
      return {
        ...state,
        visions: action.payload
      };
    case DELETE_MISSIONS:
      return {
        ...state,
        visions: state.visions.filter(vision => vision.id !== action.payload)
      };

    case ADD_MISSIONS:
      return {
        ...state,
        visions: [...state.visions, action.payload]
      };
    default:
      return state;
  }
}
