import React, { Component } from "react";
import { Form, Input, Button } from "antd";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class POST extends Component {
  state = {
    subject_name: null,
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }
  onChangesubjects = (selected) => {
    console.log(selected);
    this.setState({
      assignedsubjects: selected,
    });
  };

  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name } = this.state;
    const quizz = {
      subject_name,
    };
    quizz.subject_name = [];
    for (let i in subject_name) {
      quizz.subject_name.push(subject_name[i].value);
    }

    console.log(quizz);
    //this.props.addClos(clo);
  };

  render() {
    //console.log(this.verbsoptions());
    //console.log(this.state.subject_name);
    console.log(this.props.assignedsubjects);
    const { subject_name } = this.state;
    let optionsubjects = [];
    if (this.props.assignedsubjects.length > 0) {
      for (let i in this.props.plos) {
        optionsubjects.push({
          value: this.props.assignedsubjects[i].id,
          label: this.props.assignedsubjects[i].tittle,
        });
      }
    }

    return (
      <div>
        <Form
          name="dynamic_form_item"
          {...formItemLayoutWithOutLabel}
          onFinish={onFinish}
        >
          <Form.List name="names">
            {(fields, { add, remove }) => {
              return (
                <div>
                  {fields.map((field, index) => (
                    <Form.Item
                      {...(index === 0
                        ? formItemLayout
                        : formItemLayoutWithOutLabel)}
                      label={index === 0 ? "Passengers" : ""}
                      required={false}
                      key={field.key}
                    >
                      <Form.Item
                        {...field}
                        validateTrigger={["onChange", "onBlur"]}
                        rules={[
                          {
                            required: true,
                            whitespace: true,
                            message:
                              "Please input passenger's name or delete this field.",
                          },
                        ]}
                        noStyle
                      >
                        <Input
                          placeholder="passenger name"
                          style={{ width: "60%" }}
                        />
                      </Form.Item>
                      {fields.length > 1 ? (
                        <MinusCircleOutlined
                          className="dynamic-delete-button"
                          style={{ margin: "0 8px" }}
                          onClick={() => {
                            remove(field.name);
                          }}
                        />
                      ) : null}
                    </Form.Item>
                  ))}
                  <Form.Item>
                    <Button
                      type="dashed"
                      onClick={() => {
                        add();
                      }}
                      style={{ width: "60%" }}
                    >
                      <PlusOutlined /> Add field
                    </Button>
                  </Form.Item>
                </div>
              );
            }}
          </Form.List>

          <Form.Item>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
        ); };
        <h1>CREATE QUIZZ</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Subject Name</label>
            <input
              className="form-control"
              type="text"
              name="subject_name"
              onChange={this.onChange}
              value={subject_name}
            ></input>
          </div>
          <div className="form-group">
            <label>Create Questions</label>
            <input
              className="form-control"
              type="text"
              name="label"
              onChange={this.onChange}
              value={label}
            ></input>
          </div>
          <div className="form-group">
            <label>Choices</label>
            <input
              className="form-control"
              type="text"
              name="choice_text"
              onChange={this.onChange}
              value={code}
            ></input>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  assignedsubjects: state.assignedsubjects.assignedsubjects,
});

export default connect(mapStateToProps, {
  addAssignment,
  getAssigned,
})(POST);
