import axios from "axios";
import { GET_VISIONS, DELETE_VISIONS, ADD_VISIONS } from "./types";
import { tokenConfig } from "./auth";

//get visions
export const getVisions = () => dispatch => {
  axios
    .get("http://127.0.0.1:8000/api/vision/")
    .then(res => {
      dispatch({
        type: GET_VISIONS,
        payload: res.data
      });
    })
    .catch(err => console.log(err));
};
//delete vision
export const deleteVisions = id => dispatch => {
  axios
    .delete("http://localhost:8000/api/vision/" + id)
    .then(res => {
      dispatch({
        type: DELETE_VISIONS,
        payload: id
      });
    })
    .catch(err => console.log(err));
};

//ADD visions
export const addVision = vision => dispatch => {
  axios
    .post("http://127.0.0.1:8000/api/vision/", vision)
    .then(res => {
      dispatch({
        type: ADD_VISIONS,
        payload: res.data
      });
    })
    .catch(err => console.log(err));
};
