import React, { Component } from "react";
import Select from "react-select";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addAssignment } from "../../../actions/teacher_framework_actions/create_quizz";
import { getAssigned } from "../../../actions/teacher_framework_actions/viewassignedsubjects";

export class POST extends Component {
  state = {
    subject_name: null,
  };
  static propTypes = {
    assignedsubjects: PropTypes.func.isRequired,
    getAssigned: PropTypes.func.isRequired,
    addAssignment: PropTypes.func.isRequired,
  };

  componentDidMount() {
    this.props.getAssigned();
  }
  onChangeSubject = (selected) => {
    console.log(selected);
    this.setState({
      subject_name: selected,
    });
  };
  onChange = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });
  onSubmit = (e) => {
    e.preventDefault();
    const { subject_name } = this.state;
    const assignment = { subject_name };
    assignment.subject_name = [];
    for (let i in subject_name) {
      assignment.subject_name.push(subject_name[i].value);
    }
    console.log(assignment);
    //this.props.addPlo(plo);
  };
  render() {
    const { subject_name } = this.state;
    let optionsubjects = [];
    //console.log(this.props.subject_name.length);

    if (this.props.assignedsubjects.length > 0) {
      for (let i in this.props.subject_name) {
        optionsubjects.push({
          value: this.props.subject_name[i].id,
          label: this.props.subject_name[i].tittle,
        });
      }
    }
    return (
      <div>
        <h1>Add Quizz</h1>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Select Subject</label>
            <Select
              value={subject_name}
              onChange={this.onChangeSubject}
              options={optionsubjects}
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  assignedsubjects: state.assignedsubjects.assignedsubjects,
});

export default connect(mapStateToProps, { addAssignment, getAssigned })(POST);
