import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getVisions } from "../../actions/visions";
import { addPeo_Vision } from "../../actions/peo_visions";
import { Form } from "reactstrap";
import { Dropdown } from "semantic-ui-react";
export class MapForm extends Component {
  static propTypes = {
    visions: PropTypes.array.isRequired,
    getVisions: PropTypes.func.isRequired,
    addPeo_Vision: PropTypes.func.isRequired
  };
  onChange = e =>
    this.setState({
      [e.target.name]: e.target.value
    });
  onSubmit = e => {
    e.preventDefault();
    const { vision, peo } = this.state;
    const peo_vision = { vision, peo };
    this.props.addPeo_Vision(peo_vision);
  };
  constructor() {
    super();
    this.state = {
      Visions: []
    };
  }

  //component load then
  componentDidMount() {
    this.props.getVisions();
  }
  render() {
    const { vision } = this.state;
    let optionItems = this.props.visions.map(vision => (
      <option key={vision.id}>{vision.code}</option>
    ));

    return (
      <div>
        <form onSubmit={this.onSubmit}>
          <select name="code" onChange={this.onChange} value={code}>
            {optionItems}
          </select>

          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

//state of redux
const mapStateToProps = state => ({
  visions: state.visions.visions
});
export default connect(mapStateToProps, { getVisions, addPeo_Vision })(MapForm);
